import React from 'react';
import { Link, useMatch } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import SearchBar from '../SearchBar/SearchBar.js';
import { sortRecipes } from '../../redux/actions.js';
import S from './Nav.styled.js'

export default function NavBar() {
  const dispatch = useDispatch();
  const match = useMatch('/')

  function handleClickOrderButton(sortCriteria) {
    dispatch(sortRecipes(sortCriteria));
  }

  return (
    <S.Div >
      <SearchBar/>

      <Link to='/recipe/create'>
        <S.CreateButton>
          CREAR
        </S.CreateButton>
      </Link >

      <Link to='/'>
        <S.HomeButton>
          HOME
        </S.HomeButton>
      </Link >

      {match && <S.Dropdown>
        <S.DropdownButton>Order by</S.DropdownButton>
        <S.DropdownContent>
          <p onClick={() => handleClickOrderButton('az') }>(A-Z)</p>
          <p onClick={() => handleClickOrderButton('za') }>(Z-A)</p>
          <p onClick={() => handleClickOrderButton('score') }>score</p>
        </S.DropdownContent>
      </S.Dropdown> }
    </S.Div>
  )
};
