import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import {  useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import Loading from '../Loading/Loading.js';
import { setLoading } from '../../redux/actions.js';
import * as S from './RecipeDetail.styled.js'

export default function RecipeDetail() {
  const { id } = useParams();
  const [details, setDetails] = useState({
    title: '',
    image: '',
    diets: [],
    summary: '',
    score: '',
    healthScore: '',
    instructions: [],
  });
  const { loading } = useSelector(state => state); 
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(setLoading(true));
    (async function(id) {
      try {
        const result = await axios.get(`http://localhost:3001/recipes/detail/${id}`); 
        // Map diets property of db recipes from an array of objects to an array of strings.
        result.data.diets = !id.includes('EXTERNAL_') ? result.data.diets?.map(diet => diet.name) : result.data.diets;
        setDetails({
          ...details,
          ...result.data, 
        });
        dispatch(setLoading(false));
      } catch(error) {
        console.log(error);
      }
    })(id);
  }, [])

  return (
    <> { loading ? <Loading/> : <S.RecipeDetail>
      <S.Image src={ details.image } alt='recipe'/>

      <S.Info > 
        <S.Title>{ details.title }</S.Title>

        <S.Score percentage={ details.score }>Spoonacular score: </S.Score>
        <S.HealthScore>Health score: { details.healthScore }%</S.HealthScore>

        <S.Diets>
            { details.diets.map((diet, index) => {
              return (
                <S.Diet key={ index }>{ diet }</S.Diet>
              )
            }) }
        </S.Diets>

        <S.SubTitle>Summary:</S.SubTitle>
        <S.Text>{ details.summary }</S.Text>

        <S.SubTitle>Instructions:</S.SubTitle>
        <S.Text> 
          { details.instructions.map((instruction, index) => {
            return (
              <p key={ index }> { instruction } </p>
            )
          }) }
        </S.Text>
      </S.Info>
    </S.RecipeDetail> } </>

  )
}
