import React, {useEffect} from 'react';
import { useSelector, useDispatch} from 'react-redux'
import styled from 'styled-components'

import { filterCards, changeAppliedFilters } from '../actions';


export default function Filters() {
  const { query, allFilters, appliedFilters } = useSelector(state => state);
  const dispatch = useDispatch();

  function handleClick(event, filter) {
    event.preventDefault();
    dispatch(changeAppliedFilters(filter));
    dispatch(filterCards());
  }

  return (
    <Ul> 
      { query && allFilters.map((filter) => {
          return (
            <li key={ filter.id }> 
              <Input type='checkbox' onClick={ (event) => handleClick(event, filter.name) }/>
              { filter.name }
            </li>
          )
        }) 
      }
    </Ul>
  )
};

  // <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike">
const Ul = styled.ul`
  margin: 10px;
  list-style: none;
  float: left;
`

const Input = styled.input`
  margin: 10px;
  padding: 10px;
  background-color: red;
`
