import styled from 'styled-components';

export const SearchBar = styled.form`
  all: unset;
  display: flex;
  align-items: center;
  width: 600px;
  height: 60px;
`;

export const Query = styled.input`
  all: unset;
  padding: 0px 40px;
  height: 100%;
  width: 100%;
  border-radius: 5px;
  background-color: #FAF5E6;
  color: #000;
  font-size: 22px;
  font-weight: 600;
`;

export const Button = styled.button`
  all: unset;
  display: flex;
  align-items: center;
  margin-left: 12px;
  width: 60px;
  height: 60px;
  background-color: #2D2C2E;
  cursor: pointer;
`;

export const MagnifyingGlass = styled.svg`
  width: 38px;
  height: 38px;
  color: #FAF5E6;
  fill: currentColor;
	transition: 100ms;

  ${Button}:hover & {
    color: #FBBD0D;
  }
`;

