import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components'

import { getRecipeDetail } from '../actions';

export default function RecipeDetail() {
  const recipeDetail = useSelector(state => state.recipeDetail);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getRecipeDetail(id));
    console.log(recipeDetail.diets)
    console.log(recipeDetail)
  }, [])

  return (
    <Div>
      { recipeDetail.title &&
      <ul>
        <li>{ recipeDetail.title }</li>

        <li><img src={ recipeDetail } alt='Not found.'/></li>

        <li>
          <ul> 
            { recipeDetail.dishTypes.map((dishType, index) => {
              return (
                <li key={ index }> 
                  { dishType } 
                </li> 
              )
            }) }
          </ul>
        </li>


        <li>
          <ul> 
            { recipeDetail.diets.map((diet, index) => {
              return (
                <li key={ index }> 
                  { diet } 
                </li> 
              )
            }) }
          </ul>
        </li>

        <li>{ recipeDetail.summary }</li> 

        <li>{ recipeDetail.score }</li>

        <li>{ recipeDetail.healthScore }</li> 

        <li>
          <ul> 
            { recipeDetail.instructions.map((instruction, index) => {
              return (
                <li key={ index }> 
                  { instruction } 
                </li> 
              )
            }) }
          </ul>
        </li>
      </ul>
      }
    </Div>
  )
}

const Div = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -250px;
  margin-left: -500px;

  height: 500px;
  width: 1000px;
  font-size: 20px;
  background-color: pink;
`;
