import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Loading from '../Loading/Loading.js';
import * as S from './RecipeDetail.styled.js'

export default function RecipeDetail() {
  const { id } = useParams();
  const [status, setStatus] = useState('loading');
  const [details, setDetails] = useState({
    title: '',
    image: '',
    diets: [],
    summary: '',
    score: '',
    healthScore: '',
    instructions: [],
  });

  useEffect(() => {
    (async function(id) {
      try {
        console.log('hola')
        const response = await axios.get(`http://localhost:3001/recipes/detail/${id}`); 
        setDetails({
          ...details,
          ...response.data, 
        });
        setStatus('displaying');
      } catch(error) {
        console.log(error);
        setStatus('displaying');
      }
    })(id);
  }, [])

  return (
    <> { status === 'loading' ? <Loading/> : <S.RecipeDetail>
      <S.Image src={ details.image } alt='recipe'/>

      <S.Info > 
        <S.Title>{ details.title }</S.Title>

        <S.Score percentage={ details.score }>Spoonacular score: </S.Score>
        <S.HealthScore>Health score: { details.healthScore }%</S.HealthScore>

        <S.Diets>
            { details.diets.map((diet, index) => {
              return (
                <S.Diet key={ index }>{ diet }</S.Diet>
              )
            }) }
        </S.Diets>

        <S.SubTitle>Summary:</S.SubTitle>
        <S.Text>{ details.summary }</S.Text>

        <S.SubTitle>Instructions:</S.SubTitle>
        <S.Text> 
          { details.instructions.map((instruction, index) => {
            return (
              <p key={ index }> { instruction } </p>
            )
          }) }
        </S.Text>
      </S.Info>
    </S.RecipeDetail> } </>

  )
}
