import axios from 'axios';

export const GET_CARDS = 'GET_CARDS';
export const GET_ALL_FILTERS = 'GET_ALL_FILTERS';
export const CHANGE_APPLIED_FILTERS = 'CHANGE_APPLIED_FILTERS';
export const CHANGE_CURRENT_PAGE = 'CHANGE_CURRENT_PAGE';
export const FILTER_CARDS = 'FILTER_CARDS';
export const SORT_CARDS = 'SORT_CARDS';

export function getCards(query) {
  return async function(dispatch) {
    try {
      const result = await axios.get(`http://localhost:3001/recipes?name=${query}`);
      const cards = result.data;
      dispatch({
        type: GET_CARDS,
        payload: {cards, query}
      });
      dispatch({
        type: FILTER_CARDS,
      })
    } catch(error) {
      console.log(error);
    }
  }
};

export function getAllFilters() {
  return async function(dispatch) {
    try {
      const result = await axios.get(`http://localhost:3001/diets`);
      const filters = result.data;
      dispatch({
        type: GET_ALL_FILTERS,
        payload: filters,
      });
    } catch(error) {
      console.log(error);
    }
  }
}

export function changeAppliedFilters(filter) {
  return {
    type: CHANGE_APPLIED_FILTERS,
    payload: filter,
  }
}

export function changeCurrentPage(pageNumber) {
  return {
    type: CHANGE_CURRENT_PAGE,
    payload: pageNumber
  }
}

export function filterCards() {
  return {
    type: FILTER_CARDS,
  }
}

export function sortCards(sortCriteria) {
  return {
    type: SORT_CARDS,
    payload: sortCriteria
  }
};
