import styled, {keyframes} from 'styled-components';
import { colors } from '../GlobalStyle.styled';
const time = '1.5';
const size = '60px';

const a = keyframes`
  50 100% { transform: scale(0.1) } 
`

export const DotCero = styled.div`
  background: tomato;

  &, &:after {
    display: inline-block;
    width: ${size}; height: ${size};
    border-radius: 50%;
    animation: ${a} ${time}s ${0/5*time}s infinite
  }

  &:after {
    background: black;
    content: ${''};
  }
`

export const DotOne = styled.div`
  background: tomato;

  &, &:after {
    display: inline-block;
    width: ${size}; height: ${size};
    border-radius: 50%;
    animation: ${a} ${time}s ${1/5*time}s infinite
  }

  &:after {
    background: black;
    content: ${''};
  }
`

export const DotTwo = styled.div`
  background: tomato;

  &, &:after {
    display: inline-block;
    width: ${size}; height: ${size};
    border-radius: 50%;
    animation: ${a} ${time}s ${2/5*time}s infinite
  }

  &:after {
    background: black;
    content: ${''};
  }
`

export const DotThree = styled.div`
  background: tomato;
  display: inline-block;
  width: ${size}; height: ${size};
  border-radius: 50%;
  animation: ${a} ${time}s ${3/5*time}s infinite;

  &:after {
    background: black;
    display: inline-block;
    width: ${size}; height: ${size};
    border-radius: 50%;
    animation: ${a} ${time}s ${3/5*time}s infinite
  }
`

export const DotFour = styled.div`
  background: tomato;

  &, &:after {
    display: inline-block;
    width: ${size}; height: ${size};
    border-radius: 50%;
    animation: ${a} ${time}s ${4/5*time}s infinite
  }

  &:after {
    content: ${''};
  }
`


const rotate = keyframes`
  0% {transform: rotate(0deg)}
  100% {transform: rotate(360deg)}
`
export const Loading = styled.div`
  height: 90.4vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: black;
`

export const OuterWrapper = styled.div`
  height: 200px;
  width: 200px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: transparent;
  animation: ${rotate} 3s linear infinite;
`

export const Wrapper = styled.div`
	display: flex;
`

export const Inner = styled.div`
  margin: 10px;
  width: 60px;
  height: 60px;
  background-color: black;
  border-radius: 20px;
  box-shadow: 8px 8px 16px 2px rgba(55,55,55,0.8);
`
