import React  from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';
import Card from './Card.js'

export default function Cards() {
  const recipes  = useSelector((state) => state.recipes.displayed);
  const page = useSelector((state) => state.parameters.page);

  return (
      <Div> 
        { 
          recipes?.slice(page*9, (page + 1)*9).map(recipe => {
            return ( 
              <Card 
                key= { recipe.id }
                id={ recipe.id }
                title={ recipe.title }
                image={ recipe.image }
                dishTypes={ recipe.dishTypes }
                diets={ recipe.diets }
                score={ recipe.score }
              /> 
            )
          })
        }
      </Div> 
  )
};

const Div = styled.div`
  position: absolute;
  top: 60px;
  bottom: 0px;
  left: 300px;
  right: 0px;

  font-size: 25px;
  background-color: yellow;
`;
