import axios from 'axios'

export const GET_RECIPES = 'GET_RECIPES';
export const GET_RECIPE_DETAIL = 'GET_RECIPE_DETAIL';
export const POST_RECIPE = 'POST_RECIPE';

export const getRecipes = function(name) {
  return async (dispatch) => {
    try {
      const result = await axios.get(`http://localhost:3001/recipes?name=${name}`);
      const recipes = result.data[0];
      dispatch({
        type: GET_RECIPES,
        payload: recipes
      });
    } 

    catch(error) {
      console.log(error);
    }
  }
}

export const getRecipeDetail = function(id) {
  return async function(dispatch) {
    try {
      const recipeDetail = await axios.get(`http://localhost:3001/recipes/:${id}`); 
      dispatch({
        type: GET_RECIPE_DETAIL,
        payload: recipeDetail,
      });
    }

    catch(error) {
      console.log(error);
    }
  }
}
