import React, {useEffect} from 'react';
import { useSelector, useDispatch} from 'react-redux'
import styled from 'styled-components'

import { filterCards, changeAppliedFilters } from '../actions';


export default function Filters() {
  const { query, allFilters, appliedFilters } = useSelector(state => state);
  const dispatch = useDispatch();

  function handleClick(event, filter) {
    event.preventDefault();
    dispatch(changeAppliedFilters(filter));
    dispatch(filterCards());
  }

  return (
    <Form> 
      { query && allFilters.map((filter) => {
          return (
            <>
              <label for="vehicle1">{ filter.name }</label>
              <input type="checkbox" id={ filter.id } onClick={ (event) => handleClick(event, filter.name) }/>
            </>
          )
        }) 
      }
    </Form>
  )
};

const Form = styled.form`
  position: absolute;
  top: 300px;
  left: 0px;
  display: flex-block;
  align-items: center;
  justify-content: center;

  font-size: 30px;
  color: black;
  background-color: greenYellow;
`

const Button = styled.button`
  display: block
`
