import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

import recipesDefaultImage from '../images/recipes-default-image.jpg'

function validation(details) {
  const error = {};
  const numbersRegex = /(^[0-9][0-9]?$)|(^100$)/;
  if (details.title === '') error.title = 'Title for the recipe is required.'
  if (details.summary === '') error.summary = 'Summary for the recipe is required.'
  if (!numbersRegex.test(details.score)) error.score = 'Score must be a non-negative number lower than 100.'
  if (!numbersRegex.test(details.healthScore)) error.healthScore = 'Health score must be a non-negative number lower than 100.'
  return error
}

export default function CreateCard() {
  const navigate = useNavigate();
  const [errors, setErrors] = useState({title: '', summary: '', score: '', healthScore: '',});
  const [details, setDetails] = useState({ title: '', summary: '', score: '', healthScore: '', instructions: '',});

  function handleChange(event) {
    setDetails({
      ...details,
      [event.target.name]: event.target.value,
    });
    setErrors(validation({
      ...details,
      [event.target.name]: event.target.value,
    }));
  } 

  async function handleSubmit(e) {
    e.preventDefault();

    let data // At the end is the data!!
    if (details.title === 'asado') data = HardCodedAsado
    else if (details.title === 'tapa de asado') data = HardCodedTapaDeAsado
    else if (details.title === 'tira de asado') data = HardCodedTiraDeAsado
    else if (details.title === 'asado a la cacerola') data = HardCodedAsadoALaCacerola
    else data = details;

    try {
      const result = await axios({
        method: 'post',
        url: 'http://localhost:3001/recipes',
        headers: {}, 
        data: {...data, image: recipesDefaultImage},
        // data: details
      });
      navigate(`/recipe/detail/${result.data.id}`);
    } catch(error) {
      console.log(error);
    }
  }

  return (
    <Form onSubmit={ handleSubmit }> 
      <label htmlFor='title'>Title: </label>
      <Input name='title' value={ details.title } onChange={ handleChange }/> 
      { errors.title && <Span>{ errors.title }</Span> }

      <label htmlFor='summary'>Summary: </label>
      <Input name='summary' value={ details.summary } onChange={ handleChange }/> 
      { errors.summary && <Span>{ errors.summary }</Span> }

      <label htmlFor='score'>Score: </label>
      <Input name='score' value={ details.score } onChange={ handleChange }/> 
      { errors.score && <Span>{ errors.score }</Span> }

      <label htmlFor='healthScore'>healthScore: </label>
      <Input name='healthScore' value={ details.healthScore } onChange={ handleChange }/> 
      { errors.healthScore && <Span>{ errors.healthScore }</Span> }

      <label htmlFor='instructions'>Instructions: </label>
      <Input name='instructions' value={ details.instructions } onChange={ handleChange }/> 
      <Button type='button' onClick={ () => console.log('a')}>
        +
      </Button>

      <Button>
        Submit
      </Button>
    </Form>
  )
};

const Form = styled.form`
  position: relative;
  top: 200px;
  left: 500px;

  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  height: 400px;
  width: 1000px;
  background-color: cyan;
`;

const Input = styled.input`
  display: block;
  position: relative;

  height: 50px;
  width: 200px;
  color: black;
  border: solid black;
  margin: 10px;
`

const Button = styled.button`
  position: relative;

  height: 50px;
  width: 50px;
  color: black;
  border: solid black;
  background-color: violet;
  margin: 10px;
  padding: 10px;
`

const Span = styled.span`
  color: red;
`

