import styled from 'styled-components';

export const SearchBar = styled.form`
  all: unset;
  display: flex;
  align-items: center;
  width: 500px;
  height: 50px;
`;

export const Query = styled.input`
  all: unset;
  height: 100%;
  width: 100%;
  padding: 0px 40px;
  border-radius: 5px;
  background-color: #fff;
  color: #000;
`;

export const Button = styled.button`
  all: unset;
  display: flex;
  align-items: center;
  margin: 10px;
  width: 50px;
  height: 50px;
  background-color: #000;
  cursor: pointer;
`;

export const MagnifyingGlass = styled.svg`
  width: 24px;
  height: 24px;
  color: #fff;
  fill: currentColor;
`;
