import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import axios from 'axios';
import styled from 'styled-components';
import recipesDefaultImage from '../images/recipes-default-image.jpg'

function validation(details) {
  const error = { disableSubmit: false };
  const numbersRegex = /(^[0-9][0-9]?$)|(^100$)/;
  if (details.title === '') { error.title = 'Title for the recipe is required.'; error.disableSubmit = true }
  if (details.summary === '') { error.summary = 'Summary for the recipe is required.'; error.disableSubmit = true }
  if (!numbersRegex.test(details.score)) { error.score = 'Score must be a non-negative number lower than 100.'; error.disableSubmit = true }
  if (!numbersRegex.test(details.healthScore)) { error.healthScore = 'Health score must be a non-negative number lower than 100.'; error.disableSubmit = true }
  return error
}

export default function CreateRecipe() {
  const [errors, setErrors] = useState({title: '', summary: '', score: '', healthScore: '', disableSubmit: true});
  const [details, setDetails] = useState({ title: '', summary: '', score: '', healthScore: '', instruction: '', instructions: [], diets: []});
  const { diets } = useSelector(state => state)
  const navigate = useNavigate();

  async function handleFormSubmit(e) {
    e.preventDefault();

    let data // At the end is the data!!
    if (details.title === 'asado') data = HardCodedAsado
    else if (details.title === 'tapa de asado') data = HardCodedTapaDeAsado
    else if (details.title === 'tira de asado') data = HardCodedTiraDeAsado
    else if (details.title === 'asado a la cacerola') data = HardCodedAsadoALaCacerola
    else data = details;

    try {
      const result = await axios({
        method: 'post',
        url: 'http://localhost:3001/recipes',
        headers: {}, 
        data: {...data, image: recipesDefaultImage},
        // data: details
      });
      navigate(`/recipe/detail/${result.data.id}`);
    } catch(error) {
      console.log(error);
    }
  }

  function handleInputChange(event) {
    setDetails({
      ...details,
      [event.target.name]: event.target.value,
    });
    setErrors(validation({
      ...details,
      [event.target.name]: event.target.value,
    }));
  } 

  function handleAddInstructionButton(event) {
    setDetails({
      ...details,
      instruction: '',
      instructions: [...details.instructions, details.instruction]
    });
  }

  function handleRemoveInstructionButton(instructionIndexToBeRemoved) {
    setDetails({
      ...details,
      instructions: details.instructions.filter((instruction, index) =>  index !== instructionIndexToBeRemoved),
    })
  }

  function handleAddDietButton(diet) {
    !details.diets.includes(diet) && setDetails({
      ...details,
      diets: [...details.diets, diet]
    }) 
  }

  function handleRemoveDietButton(dietIndexToBeRemoved) {
    setDetails({
      ...details,
      diets: details.diets.filter((instruction, index) =>  index !== dietIndexToBeRemoved),
    })
  }

  return (
    <Form onSubmit={ handleFormSubmit }> 
      <label htmlFor='title'>Title: </label>
      <Input name='title' value={ details.title } onChange={ handleInputChange }/> 
      { errors.title && <Span>{ errors.title }</Span> }

      <label htmlFor='summary'>Summary: </label>
      <Input name='summary' value={ details.summary } onChange={ handleInputChange }/> 
      { errors.summary && <Span>{ errors.summary }</Span> }

      <label htmlFor='score'>Score: </label>
      <Input name='score' value={ details.score } onChange={ handleInputChange }/> 
      { errors.score && <Span>{ errors.score }</Span> }

      <label htmlFor='healthScore'>healthScore: </label>
      <Input name='healthScore' value={ details.healthScore } onChange={ handleInputChange }/> 
      { errors.healthScore && <Span>{ errors.healthScore }</Span> }

      <label htmlFor='instruction'>Add instruction:</label>
      <Input name='instruction' value={ details.instruction } onChange={ handleInputChange }/> 
      <Button type='button' onClick={ handleAddInstructionButton }>+</Button>
      { 
        details.instructions.map((instruction, index) => {
          return (
            <p key={ index }> 
              { instruction } <Button type='button' onClick={ () => handleRemoveInstructionButton(index) }>-</Button>
            </p>
          )
        }) 
      }

      <Dropdown>
        <DropdownButton>Diets</DropdownButton>
        <DropdownContent> 
          {
            diets.name?.map((diet, index) => {
              return (
                <p key={ index } onClick={ () => handleAddDietButton(diet.name) }>{ diet.name }</p>
              )
            })
          }
        </DropdownContent>
      </Dropdown> 
      { 
        details.diets.map((diet, index) => {
          return (
            <p key={ index }> 
              { diet } <Button type='button' onClick={ () => handleRemoveDietButton(index) }>-</Button>
            </p>
          )
        }) 
      }

      <Button disabled={ errors.disableSubmit }>
        Submit
      </Button>
    </Form>
  )
};

const DropdownContent = styled.div`
  display: none;
  top: 60px;
  position: absolute;
  background-color: #f1f1f1;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  z-index: 1;

  p {
  color: black;
  padding: 12px 16px;
  text-decoration: none;
  display: block;

  &:hover {background-color: #ddd;}
  }
`

const Dropdown = styled.div`
  position: absolute;
  right: 120px;
  display: inline-block;

  &:hover ${DropdownContent} {display: block;}
`

const DropdownButton = styled.div`
  background-color: #04AA6D;
  color: white;
  padding: 16px;
  font-size: 16px;
  border: none;
  width: 80px;
  height: 28px;

  &:hover {background-color: #3e8e41;}
`

const Form = styled.form`
  position: relative;
  top: 200px;
  left: 500px;

  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  height: 400px;
  width: 1000px;
  background-color: cyan;
`;

const Input = styled.input`
  display: block;
  position: relative;

  height: 50px;
  width: 200px;
  color: black;
  border: solid black;
  margin: 10px;
`

const Button = styled.button`
  position: relative;

  height: 50px;
  width: 50px;
  color: black;
  border: solid black;
  background-color: violet;
  margin: 10px;
  padding: 10px;
`

const Span = styled.span`
  color: red;
`

// HARDCODED DATA
// HARDCODED DATA
// HARDCODED DATA
// HARDCODED DATA
const HardCodedAsado = { title: 'Asado', summary: 'God', score: 100*Math.random(), healthScore: 100*Math.random(), instructions: ['Asar asado'],};
const HardCodedTapaDeAsado = { title: 'Tapa de asado', summary: 'God', score: 100*Math.random(), healthScore: 100*Math.random(), instructions: ['Tapar asado'],};
const HardCodedTiraDeAsado = { title: 'Tira de asado', summary: 'God', score: 100*Math.random(), healthScore: 100*Math.random(), instructions: ['Tirar asado al BOTE de basura'],};
const HardCodedAsadoALaCacerola = { title: 'Asado a la cacerola', summary: 'God', score: 100*Math.random(), healthScore: 100*Math.random(), instructions: ['Cacerolear el asado en agua a 200°'],};
