import axios from 'axios';

export const GET_CARDS = 'GET_CARDS';
export const GET_FILTERS = 'GET_FILTERS';
export const SET_APPLIED_FILTERS = 'SET_APPLIED_FILTERS';
export const SET_CURRENT_PAGE = 'SET_CURRENT_PAGE';
export const FILTER_CARDS = 'FILTER_CARDS';
export const SORT_CARDS = 'SORT_CARDS';

export function getCards(query) {
  return async function(dispatch) {
    try {
      const result = await axios.get(`http://localhost:3001/recipes?name=${query}`);
      const cards = result.data;
      dispatch({
        type: GET_CARDS,
        payload: cards,
      });
    } catch(error) {
      console.log(error);
    }
  }
};

export function getFilters() {
  return async function(dispatch) {
    try {
      const result = await axios.get(`http://localhost:3001/diets`);
      const filters = result.data;
      dispatch({
        type: GET_FILTERS,
        payload: filters,
      });
    } catch(error) {
      console.log(error);
    }
  }
}

export function setFilters(filter) {
  return {
    type: SET_APPLIED_FILTERS,
    payload: filter,
  }
}

export function setCurrentPage(pageNumber) {
  return {
    type: SET_CURRENT_PAGE,
    payload: pageNumber
  }
}

export function filterCards() {
  return {
    type: FILTER_CARDS,
  }
}

export function sortCards(sortCriteria) {
  return {
    type: SORT_CARDS,
    payload: sortCriteria
  }
};
