import styled from 'styled-components';

export const ParametersContainer = styled.div`
  position: sticky;
  top: 10px;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  height: 800px;
  color: #2D2C2E;
  font-size: 1.4rem;
`

export const Diets = styled.div`
  margin-top: auto;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`

export const Diet = styled.span`
  margin: 10px;
