  background-color: ${props => props.active ? '#FD1F4A' : '#2D2C2E'};
  border-radius: 80px;
  color: #FAF5E6;
  text-align: center;
  cursor: pointer;
  transition: 100ms;

  &:hover {
    background-color: ${props => props.active ? '#ED0F3A' : '#1D1C1E'};
  }
`

export const DropdownButton = styled.div`
  height: 55px;
  padding: 12px;
  background-color: #2D2C2E;
  color: #FAF5E6;
  text-align: center;
  font-size: 1.1rem;

  &:hover {
    background-color: #1D1C1E; 
  }
`

