  visibility: hidden;
  height: 0;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  opacity: 0;
  transition: visibility 200ms, opacity 200ms linear;

  p {
    padding: 12px 16px;

    &:hover {
      background-color: #EAE5D6;
    }
  }
`

export const Dropdown = styled.div`
  // height: 20px;
  width: 240px;
  margin: 25px 0px;
  &:hover ${DropdownContent} {
    height: auto;
