import styled from 'styled-components';

export const SearchBar = styled.form`
  all: unset;
  display: flex;
  align-items: center;
  width: 600px;
  height: 60px;
`;

export const Query = styled.input`
  all: unset;
  padding: 0px 40px;
  height: 100%;
  width: 100%;
  border-radius: 5px;
  background-color: #fff;
  color: #000;
  font-size: 22px;
  font-weight: 600;
`;

export const Button = styled.button`
  all: unset;
  display: flex;
  align-items: center;
  margin-left: 12px;
  width: 60px;
  height: 60px;
  background-color: #000;
  cursor: pointer;
`;

export const MagnifyingGlass = styled.svg`
  width: 38px;
  height: 38px;
  color: #fff;
  fill: currentColor;

  ${Button}:hover & {
    color: #FFC371;
  }
`;
