import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import styled from 'styled-components';

import { filterCards, getCards } from '../actions';
import './SearchBar.module.css';

export default function SearchBar() {
  const [query, setQuery] = useState('')
  const dispatch = useDispatch();
  const navigate = useNavigate();

  function handleChange(event) {
    setQuery(event.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    query !== '' && dispatch(getCards(query));
    setQuery('')
    navigate('/');
  }

  return (
    <Div >
      <Form onSubmit={ handleSubmit }> 
        <Input 
          type='text' 
          name='query' 
          placeholder='Search...'
          onChange={ handleChange }
          value={ query }
        />

        <Button>
          <Svg viewBox="0 0 1024 1024">
            <path 
              className="path1" 
              d="M848.471 928l-263.059-263.059c-48.941 36.706-110.118
              55.059-177.412 55.059-171.294 0-312-140.706-312-312s140.706-312
              312-312c171.294 0 312 140.706 312 312 0 67.294-24.471
              128.471-55.059 177.412l263.059 263.059-79.529
              79.529zM189.623 408.078c0 121.364 97.091 218.455 218.455
              218.455s218.455-97.091
              218.455-218.455c0-121.364-103.159-218.455-218.455-218.455-121.364
              0-218.455 97.091-218.455 218.455z">
            </path>
          </Svg>
        </Button>
      </Form>
    </Div>
  )
};

const Form = styled.form`
  background-color: #4654e1;
  width: 300px;
  height: 40px;
  border-radius: 5px;
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const Input = styled.input`
  all: unset;
  font: 16px system-ui;
  color: #fff;
  height: 100%;
  width: 100%;
  padding: 6px 10px;
`;

/* ::placeholder { */
  /* color: #fff; */
  /* opacity: 0.7;  */
/* } */

const Button = styled.button`
  all: unset;
  cursor: pointer;
  width: 44px;
  height: 44px;
`;

const Svg = styled.svg`
  color: #fff;
  fill: currentColor;
  width: 24px;
  height: 24px;
  padding: 10px;
`;



const Div = styled.div`
  position: 'absolute',
  left: '0px',
  display: 'flex',
  alignItems: 'center',
  height: '100%',
  backgroundColor: 'red',
`;
