import React from 'react';
import { Link } from 'react-router-dom';
import * as S from './Recipe.styled.js';

export default function Recipe(props) {

   return (
      <S.Recipe>
         <S.Image src={ props.image } alt='recipe' />

         <S.Info> 
            <S.Title>{ props.title.length > 22 ? props.title.slice(0,22) + '...' : props.title }</S.Title> 

            <S.Score raiting="2.9">
               { props.score }
            </S.Score>


            <S.StarScore> 
            <div className="stars"  aria-label="Rating of this product is 2.3 out of 5."></div>

            </S.StarScore>

            <S.Diets>
               { props.diets.map((diet, index) => {
               return (
                  <S.Diet key={ index }>{ diet }</S.Diet>
               )
               }) }
            </S.Diets>
         </S.Info>


         <Link to={ `/recipe/detail/${ props.id }` }>
            <S.DetailButton>
               LET'S COOK!
            </S.DetailButton>
         </Link>
      </S.Recipe>
   )
};




