import { GET_RECIPES, GET_DIETS, CHANGE_FILTERS, FILTER_RECIPES, SORT_RECIPES, SET_PAGE, SET_LOADING } from './actions.js';

const initialState = {
  diets: [],

  recipes: {
    fetched: [],
    displayed: [],
  },

  parameters: {
    displaying: false,
    page: 0,
    filters: [],
  },

  loading: false,
};

export default function reducer(state = initialState, { type, payload}) {
  switch(type) {
    case GET_DIETS:
    // Map diets from an array of objects to an array of strings
    payload = payload.map(diet => diet.name)
      return {
        ...state,
        diets: payload,
      }

    case GET_RECIPES: 
    // Map diets property of db recipes from an array of objects to an array of strings
      payload[0] = payload[0].map(dbRecipe => ({...dbRecipe, diets: dbRecipe.diets.map(diet => diet.name)}))
      return {
        ...state,
        recipes: {
          fetched: [...payload[0], ...payload[1]],
          displayed: [...payload[0], ...payload[1]]
        },
        parameters: {
          displaying: true,
          page: 0, 
          filters: [],
        },
        loading: false
      };

    case CHANGE_FILTERS: 
      return {
        ...state,
        parameters: {
          ...state.parameters,
          filters: changeFilters([...state.parameters.filters], payload)
        }
      }

    case FILTER_RECIPES:
      return {
        ...state,
        recipes: {
          ...state.recipes,
          displayed: filterRecipes([...state.recipes.fetched], [...state.parameters.filters]) 
        },
        parameters: {
          ...state.parameters,
          page: 0,
        }
      }

    case SORT_RECIPES:
      return {
        ...state,
        recipes: {
          ...state.recipes,
          displayed: sortRecipes([...state.recipes.displayed], payload),
        },
        parameters: {
          ...state.parameters,
          page: 0,
        }
      }

    case SET_PAGE: 
      return {
        ...state,
        parameters: {
          ...state.parameters,
          page: payload 
        }
      }

    case SET_LOADING:
      return {
        ...state,
        loading: payload
      }

    default:
      return {
        ...state,
      };
  }
};

function changeFilters(filters, filter) {
  filters.includes(filter) 
    ? filters.splice(filters.indexOf(filter), 1) 
    : filters.push(filter)
  return filters;
}

function filterRecipes(recipes, filters) {
  return recipes.filter(recipe => {
    return filters.every(filter => recipe.diets.includes(filter))
  })
};

function sortRecipes(recipes, sortCriteria) {
  switch(sortCriteria) {
    case 'az': return recipes.sort((x, y) => x.title.localeCompare(y.title));
    case 'za': return recipes.sort((x, y) => y.title.localeCompare(x.title));
    case 'score': return recipes.sort((x, y) => y.score - x.score);
    default: return recipes;
  }
};
