import React, {useState} from 'react';
import styled from 'styled-components';

export default function CreateRecipe() {
  const [details, setDetails] = useState({
    title: '',
    summary: '',
    score: '',
    healthScore: '',
    instructions: '',
  });

  function handleChange(event) {
    setDetails({
      ...details,
      [event.target.name]: event.target.value,
    })
  } 

  return (
    // <Div>
      <Form> 
        <Input name='title' value={ details.title } onChange={ handleChange }/> 
        <Input name='summary' value={ details.summary } onChange={ handleChange }/> 
        <Input name='score' value={ details.score } onChange={ handleChange }/> 
        <Input name='healthScore' value={ details.healthScore } onChange={ handleChange }/> 
        <Input name='instructions' value={ details.instructions } onChange={ handleChange }/> 
      </Form>
    // </Div>
  )
};

const Form = styled.form`
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -250px;
  margin-left: -500px;

  display: flex;
  align-items: center;
  height: 500px;
  width: 1000px;
  background-color: cyan;
`;

const Input = styled.input`
  position: relative;

  border: solid black;
  margin: 100px;
  display: block;
`
