import { SET_RECIPES, SET_RECIPE_DETAIL } from '../actions';

const initialState = {
  recipes: [],
  recipeDetail: {}
};

export default function reducer(state = initialState, action) {
  switch(action.type) {
    case SET_RECIPES: 
      return {
        ...state,
        [action.statePropperty]: action.payload,
      };
    case SET_RECIPE_DETAIL: 
      return {
        ...state,
        recipeDetail: action.payload,
      };
    default:
      return {
        ...state,
      };
  }
};
