import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import CreateRecipeDumb from './CreateRecipeDumb';
import recipesDefaultImage from '../../images/12bf6597aab3bc166a63e9e793945367.svg.jpg'
import { Asado, TapaDeAsado, TiraDeAsado, AsadoALaCacerola } from '../hardCodedData.js'

function validation(details) {
  const error = { disableSubmit: false };
  const numbersRegex = /(^[0-9][0-9]?$)|(^100$)/;
  if (details.title === '') { error.title = true; error.disableSubmit = true }
  if (details.summary === '') { error.summary = true; error.disableSubmit = true }
  if (!numbersRegex.test(details.score) && details.score !== '') { error.score = true; error.disableSubmit = true }
  if (!numbersRegex.test(details.healthScore) && details.healthScore !== '') { error.healthScore = true; error.disableSubmit = true }
  return error
}

export default function CreateRecipeSmart() {
  const [errors, setErrors] = useState({title: false, summary: false, score: false, healthScore: false, disableSubmit: true});
  const [details, setDetails] = useState({ title: '', summary: '', score: '', healthScore: '', instruction: '', instructions: [], diets: []});
  const navigate = useNavigate();

  async function handleFormSubmit(e, selectedDiets) {
    e.preventDefault();

    // HARDCODED DATA!!!!
    // HARDCODED DATA!!!!
    let data
    if (details.title === 'asado') data = Asado
    else if (details.title === 'tapa de asado') data = TapaDeAsado
    else if (details.title === 'tira de asado') data = TiraDeAsado
    else if (details.title === 'asado a la cacerola') data = AsadoALaCacerola
    else data = details;

    try {
      const result = await axios({
        method: 'post',
        url: 'http://localhost:3001/recipes',
        headers: {}, 
        data: {...data, diets: selectedDiets, image: recipesDefaultImage},
        // data: details
      });
      navigate(`/recipe/detail/${result.data.id}`);
    } catch(error) {
      console.log(error);
    }
  }

  function handleInputChange(event) {
    setDetails({
      ...details,
      [event.target.name]: event.target.value,
    });
    setErrors(validation({
      ...details,
      [event.target.name]: event.target.value,
    }));
  } 

  function handleAddInstruction(event) {
    if (details.instruction === '') return;
    setDetails({
      ...details,
      instruction: '',
      instructions: [...details.instructions, details.instruction]
    });
  }

  function handleRemoveInstruction(instructionIndexToBeRemoved) {
    setDetails({
      ...details,
      instructions: details.instructions.filter((instruction, index) =>  index !== instructionIndexToBeRemoved),
    })
  }

  function handleAddDiet(diet) {
    !details.diets.includes(diet) && setDetails({
      ...details,
      diets: [...details.diets, diet]
    }) 
  }

  function handleRemoveDiet(dietIndexToBeRemoved) {
    setDetails({
      ...details,
      diets: details.diets.filter((instruction, index) =>  index !== dietIndexToBeRemoved),
    })
  }

  return  <CreateRecipeDumb 
      details={ details }
      errors={ errors }
      handleFormSubmit={ handleFormSubmit }
      handleInputChange={ handleInputChange }
      handleAddInstruction={ handleAddInstruction }
      handleRemoveInstruction={ handleRemoveInstruction }
      handleAddDiet={ handleAddDiet }
      handleRemoveDiet={ handleRemoveDiet }
    />
};
