import styled from 'styled-components';
import { colors } from '../GlobalStyle.styled';

export const Recipe = styled.div`
	position: relative;
	margin: 15px;
	height: 480px;
	width: 435px;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
  box-shadow: 0px 8px 16px 0px rgba(155,155,155,0.4);
	background-color: ${colors.WHITE};
`

export const Image = styled.img`
	max-height: 280px;
	height: 4000px
	background-size: cover;
`

export const Info = styled.div`
	height: 100%;
	display: flex;
	flex-direction: column;
	justify-content: space-between;
`

export const Title = styled.h1`
	margin: 20px 30px 0px;
	max-height: 40px;
	width: auto;
	color: ${colors.BLACK};
	text-align: left;
	font-size: 1.4rem;
	font-weight: normal;
	letter-spacing: 0.1em;
	text-transform: uppercase;
	white-space: nowrap;
  overflow-x: scroll;

	::-webkit-scrollbar {
		display: none;  /* Hide scrollbar in Chrome, Safari and Opera. */
		scrollbar-width: none;  /* Hide in Firefox. */
		-ms-overflow-style: none;  /* Hide in IE and Edge. */
	}
`

export const Score = styled.div`
	margin: 0px 10px 5px;
	font-size: 24px;

	&:before {
		content: '★★★★★';
		font-size: 40px;
		background: ${(props) => `linear-gradient(90deg, #FBBD0D ${props.percentage}%, #2D2C2E ${props.percentage}%)`};
		background-clip: text;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
	}

`

export const Diets = styled.ul`
	margin: 5px 5px 20px;
	width: auto;
	display: flex;
	flex-wrap: no-wrap;
  overflow-x: scroll;

	::-webkit-scrollbar {
		display: none;  /* Hide scrollbar in Chrome, Safari and Opera. */
		scrollbar-width: none;  /* Hide in Firefox. */
		-ms-overflow-style: none;  /* Hide in IE and Edge. */
	}
`

export const Diet = styled.li`
	flex-shrink: 0;
	margin: 0px 5px;
	padding: 2px 10px;
	width: auto;
	font-size: 0.8rem;
	text-align: center;
	border-radius: 20px;
	background-color: ${colors.BLACK};
	color: ${colors.WHITE};
`

export const DetailButton = styled.button`
	padding: 18px;
	width: 100%;
	text-align: center;
	letter-spacing: 0.1em;
	background-color: ${colors.RED};
	color: ${colors.WHITE};
	transition: 250ms;

	&:hover {
		background: ${colors.YELLOW};
	}
`
