import { GET_CARDS, SET_PAGE, SORT_CARDS } from '../actions';

const initialState = {
  cards: [],
  pages: [[],],
  page: 1,
  sortCriteria: 'none',
};

export default function reducer(state = initialState, action) {
  switch(action.type) {
    case GET_CARDS: 
      const cards = action.payload
      return {
        ...state,
        cards,
      };

    case SET_PAGE:
      return {
        ...state,
        page: action.payload,
      }

    case SORT_CARDS:
      return {
        ...state,
        cards: sortFunction([...state.cards], action.payload),
        sortCriteria: action.payload,
      }

    default:
      return {
        ...state,
      };
  }
};

function buildPages(array, pages) {
  array.forEach((element) => {
    let lastPage = pages[pages.length - 1];
    if (lastPage.length === 9) {
      pages.push([]);
      lastPage = pages[pages.length - 1];
    }
  })
}

function sortFunction(array, sortCriteria) {
  switch(sortCriteria) {
    case 'az': return array.sort((x, y) => x.title.localeCompare(y.title));
    case 'za': return array.sort((x, y) => y.title.localeCompare(x.title));
    case 'score': return array.sort((x, y) => y.score - x.score);
    default: return array;
  }
}
