import React from 'react';
import { Link, useMatch } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components'

import SearchBar from './SearchBar.js';
import { sortCards } from '../actions';
import { dropbtn, dropdown, dropdownContent, } from './Nav.module.css'

export default function Nav() {
  const { query } = useSelector(state => state)
  const dispatch = useDispatch();
  const match = useMatch('/')

  function handleClickOrderButton(sortCriteria) {
    dispatch(sortCards(sortCriteria));
  }

  return (
    <Div >
      <SearchBar/>

      <span style={ { position: 'absolute', top: '20px', left: '200px' } }>{ query }</span>

      <Link to='/recipe/create'>
        <CreateButton>
          CREAR
        </CreateButton>
      </Link >

      <Link to='/'>
        <HomeButton>
          HOME
        </HomeButton>
      </Link >

      { match && <div className={ dropdown }>
        <button className={ dropbtn }>Order by</button>
        <div className={ dropdownContent }>
          <p onClick={() => handleClickOrderButton('az') }>(A-Z)</p>
          <p onClick={() => handleClickOrderButton('za') }>(Z-A)</p>
          <p onClick={() => handleClickOrderButton('score') }>score</p>
        </div>
      </div> }
    </Div>
  )
};

const Div = styled.div`
  position: absolute;
  top: 0px;
  left: 0px;
  right: 0px;

  height: 60px;
  background-color: orange;
`;

const CreateButton = styled.button`
  position: absolute;
  top: 0px;
  right: 10px;

  height: 60px;
  width: 50px;
  color: black;
  background-color: crimson;
`;

const HomeButton = styled.button`
  position: absolute;
  top: 0px;
  right: 62px;

  height: 60px;
  width: 50px;
  color: black;
  background-color: crimson;
`;
