import styled from 'styled-components';

export const DisplayParameters = styled.div`
  top: 10px;
  min-height: 1120px;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  font-size: 1.4rem;
  color: #2D2C2E;
`

export const Filters = styled.div`
  margin-top: auto;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  box-shadow: 0px 8px 16px 0px rgba(55,55,55,0.8);
`

export const FiltersTitle = styled.h1`
  margin: 20px 20px 40px;
  text-align: center;
  font-size: 1.8rem;
  color: white;
`

export const Filter = styled.span`
  cursor: pointer;
  height: 40px;
  margin: 10px;
  padding: 6px 24px;
  border-radius: 80px;
  text-align: center;
  background-color: ${props => props.active ? '#FD1F4A' : '#111'};
  color: #FAF0E0;
  transition: 100ms;

  &:hover {
    background-color: ${props => props.active ? '#ED0F3A' : 'black'};
  }
`

export const DropdownButton = styled.div`
  padding: 9px;
  height: 55px;
  text-align: center;
  font-size: 1.4rem;
  font-weight: 600;
  box-shadow: 0px 8px 16px 0px rgba(55,55,55,0.8);
  background-color: black;
  color: white;
  transition: 200ms;

  &:hover {
    background-color: #111; 
  }
`

export const DropdownContent = styled.div`
  visibility: hidden;
  height: 0;
  font-weight: 600;
  box-shadow: 0px 8px 16px 0px rgba(55,55,55,0.8);
  opacity: 0;
  color: white;
  transition: visibility 200ms, opacity 200ms linear;

  p {
    padding: 12px 16px;
    font-size: 1.2rem;

    &:hover {
      background-color: white;
      color: black;
    }
  }
`

export const OrderBy = styled.div`
  margin: 25px 0px;
  width: 255px;

  &:hover ${DropdownContent} {
    visibility: visible;
    height: auto;
    opacity: 1;
  }

  &:hover ${DropdownButton} {
    box-shadow: 0px 0px 0px 0px;
  }
`
