import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router';

import { getRecipes } from '../actions';
import './SearchBar.module.css';

// TODO: APPLY THIS STYLE (POSITION STYLE) TO FORM AND DELETE DIV
const searchBarStyle = {
  position: 'absolute',
  left: '0px',
  display: 'flex',
  alignItems: 'center',
  height: '100%',
  backgroundColor: 'red',
};




export default function SearchBar() {
  const [searchValue, setSearchValue] = useState('');
  const recipes = useSelector(state => state.recipes); 
  const dispatch = useDispatch();
  const navigate = useRef(useNavigate());

  function handleSubmit(e) {
    e.preventDefault();
    if (searchValue === '') return;
    dispatch(getRecipes(searchValue));
    setSearchValue('');
  }

  function handleChange(e) {
    setSearchValue(e.target.value);
  }

  useEffect(() => {
    navigate('/');
    console.log(recipes);
  }, [recipes])

  return (
    <div style={ searchBarStyle }>
      <form onSubmit={ handleSubmit }> 
        <input 
          type='text' 
          value={ searchValue }
          onChange={ handleChange }
          name='query' 
          id='query' 
          placeholder='Search...'
        />

        <button>
          <svg viewBox="0 0 1024 1024">
            <path 
              className="path1" 
              d="M848.471 928l-263.059-263.059c-48.941 36.706-110.118
              55.059-177.412 55.059-171.294 0-312-140.706-312-312s140.706-312
              312-312c171.294 0 312 140.706 312 312 0 67.294-24.471
              128.471-55.059 177.412l263.059 263.059-79.529
              79.529zM189.623 408.078c0 121.364 97.091 218.455 218.455
              218.455s218.455-97.091
              218.455-218.455c0-121.364-103.159-218.455-218.455-218.455-121.364
              0-218.455 97.091-218.455 218.455z">
            </path>
          </svg>
        </button>
      </form>
    </div>
  )
};
