import { GET_CARDS, GET_ALL_FILTERS, SET_APPLIED_FILTERS, SET_CURRENT_PAGE, FILTER_CARDS, SORT_CARDS } from '../actions';

const initialState = {
  cards: [],
  allFilters: [],
  appliedFilters: [],
  currentPage: 0,
  filteredCards: [],
  sortCriteria: 'none',
};

export default function reducer(state = initialState, { type, payload}) {
  switch(type) {
    case GET_CARDS: 
      return {
        ...state,
        cards: payload,
        currentPage: 0,
        filteredCards: payload,
      };

    case GET_ALL_FILTERS:
      return {
        ...state,
        allFilters: payload,
      }

    case SET_APPLIED_FILTERS: 
      console.log(state.appliedFilters)
      return {
        ...state,
        appliedFilters: setAppliedFiltersManager([...state.appliedFilters], payload)
      }

    case SET_CURRENT_PAGE:
      return {
        ...state,
       currentPage: payload,
      }

    case FILTER_CARDS:
      console.log(state.filteredCards)
      return {
        ...state,
        filteredCards: filterCardsManager([...state.cards], [...state.appliedFilters])
      }

    case SORT_CARDS:
      return {
        ...state,
        cards: sortCardsManager([...state.cards], payload),
        currentPage: 0,
        sortCriteria: payload,
      }

    default:
      return {
        ...state,
      };
  }
};

function setAppliedFiltersManager(appliedFilters, filter) {
  appliedFilters.includes(filter) 
    ? appliedFilters.splice(appliedFilters.indexOf(filter), 1) 
    : appliedFilters.push(filter)
  return appliedFilters;
}

function filterCardsManager(cards, appliedFilters) {
  return cards.filter(card => {
    return appliedFilters.every(filter => card.diets.includes(filter))
  })
};

function sortCardsManager(array, sortCriteria) {
  switch(sortCriteria) {
    case 'az': return array.sort((x, y) => x.title.localeCompare(y.title));
    case 'za': return array.sort((x, y) => y.title.localeCompare(x.title));
    case 'score': return array.sort((x, y) => y.score - x.score);
    default: return array;
  }
};
