import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { filterRecipes, changeFilters, sortRecipes } from '../../redux/actions.js';
import * as S from './DisplayParameters.styled.js';


export default function DisplayParameters() {
  const [selectedFilters, setSelectedFilters] = useState([])
  const { diets } = useSelector(state => state)
  const { displaying }= useSelector(state => state.parameters);
  const dispatch = useDispatch();

  function handleClickFilterButton(filter) {
    const index = selectedFilters.indexOf(filter);
    index !== -1
      ? setSelectedFilters(selectedFilters.filter(selectedFilter => selectedFilter !== filter))
      : setSelectedFilters([...selectedFilters, filter]);

    dispatch(changeFilters(filter));
    dispatch(filterRecipes());
  }

  function handleClickOrderButton(sortCriteria) {
    dispatch(sortRecipes(sortCriteria));
  }


  return (
    <>{ displaying && <S.ParametersContainer>
      <S.Dropdown>
        <S.DropdownButton>Order by</S.DropdownButton>
        <S.DropdownContent>
          <p onClick={() => handleClickOrderButton('az') }>(a-z)</p>
          <p onClick={() => handleClickOrderButton('za') }>(z-a)</p>
          <p onClick={() => handleClickOrderButton('score') }>score</p>
        </S.DropdownContent>
      </S.Dropdown>

      <S.Diets > 
        <S.Title> 
          Filter by 
        </S.Title>
        { diets.map((diet, index) => {
            const active = selectedFilters.includes(diet)
            return (
              <S.Diet 
                key={ index }
                active={ active }
                onClick={ () => handleClickFilterButton(diet, index) }>
                { diet }
              </S.Diet>
            )
          }
        ) }
      </S.Diets>
    </S.ParametersContainer> }</>
  )
};
