import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import * as S from './CreateRecipe.styled'

export default function CreateRecipeDumb({ 
  details, errors, handleFormSubmit, handleInputChange,
  handleAddInstruction, handleRemoveInstruction, handleAddDiet, handleRemoveDiet
}) {
  const [selectedDiets, setSelectedDiets] = useState([])
  const { diets } = useSelector(state => state)

  function handleClickDiet(filter) {
    const index = selectedDiets.indexOf(filter);
    index !== -1
      ? setSelectedDiets(selectedDiets.filter(selectedFilter => selectedFilter !== filter))
      : setSelectedDiets([...selectedDiets, filter]);
  }

  return (
    <S.Form onSubmit={ (e) => handleFormSubmit(e, selectedDiets) }> 
      <S.Diets > 
        <S.DietsTitle> 
          Add diets: 
        </S.DietsTitle>
        { diets.map((diet, index) => {
            const active = selectedDiets.includes(diet)
            return (
              <S.Diet 
                key={ index }
                active={ active }
                onClick={ () => handleClickDiet(diet, index) }>
                { diet }
              </S.Diet>
            )
          }
        ) }
      </S.Diets>

      <S.Inputs > 
        <S.Label htmlFor='title'>TITLE: </S.Label>
        <S.Input maxLength='100' autoComplete='off' name='title' value={ details.title } onChange={ handleInputChange }/> 
        { <S.Error active={ errors.title }>Title for the recipe is required.</S.Error> }

        <S.Label htmlFor='score'>SCORE: </S.Label>
        <S.Input name='score' autoComplete='off' value={ details.score } onChange={ handleInputChange }/> 
        { <S.Error active={ errors.score }>Score must be a non-negative integer lower than 100.</S.Error> }

        <S.Label htmlFor='healthScore'>HEALTH SCORE: </S.Label>
        <S.Input name='healthScore' autoComplete='off' value={ details.healthScore } onChange={ handleInputChange }/> 
        { <S.Error active={ errors.healthScore }>Health score must be a non-negative integer lower than 100.</S.Error> }

        <S.Label htmlFor='summary'>SUMMARY: </S.Label>
        <S.TextArea name='summary' autoComplete='off' value={ details.summary } onChange={ handleInputChange }></S.TextArea> 
        { <S.Error active={ errors.summary }>Summary for the recipe is required.</S.Error> }

        <S.Label htmlFor='instruction'>ADD INSTRUCTION: </S.Label>
        <S.AddInstructionContainer>
        <S.TextArea name="instruction" autoComplete='off' value={ details.instruction } onChange={ handleInputChange }></S.TextArea>
        <S.AddInstruction type='button' onClick={ handleAddInstruction }>+</S.AddInstruction>
        </S.AddInstructionContainer>

        <S.Instructions> 
          { details.instructions.map((instruction, index) => {
              return (
                <p key={ index }> 
                  { instruction } <S.RemoveInstruction type='button' onClick={ () => handleRemoveInstruction(index) }>-</S.RemoveInstruction>
                </p>
              )
            }) }
        </S.Instructions>

      </S.Inputs>

      <S.SubmitButton disabled={ errors.disableSubmit } inactive={errors.disableSubmit }>
        <S.P>SUBMIT</S.P>
      </S.SubmitButton>
    </S.Form>
  )
};
