import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import styled from 'styled-components'
import axios from 'axios';

export default function RecipeDetail() {
  const { id } = useParams();
  const [details, setDetails] = useState({
    title: '',
    image: '',
    diets: [],
    summary: '',
    score: '',
    healthScore: '',
    instructions: [],
  });

  useEffect(() => {
    (async function(id) {
      try {
        const result = await axios.get(`http://localhost:3001/recipes/${id}`); 
        // Map diets property of db recipes from an array of objects to an array of strings
        console.log(result.data)
        console.log(result.data.diets)
        result.data.diets = !id.includes('EXTERNAL_') && result.data.diets?.map(diet => diet.name);
        setDetails({
          ...details,
          ...result.data, 
        });
      } catch(error) {
        console.log(error);
      }
    })(id);
  }, [])

  return (
    <Div>
      <ul>
        <li>{ details.title }</li>

        <li><img src={ details.image } alt='recipe'/></li>

        <li>
          <ul> 
            { details.diets?.map((diet, index) => {
              return (
                <li key={ index }>{ diet }</li> 
              )
            }) }
          </ul>
        </li>

        <li>{ details.summary }</li> 

        <li>{ details.score }</li>

        <li>{ details.healthScore }</li> 

        <li>
          <ul> 
            { details.instructions.map((instruction, index) => {
              return (
                <li key={ index }> { instruction } </li> 
              )
            }) }
          </ul>
        </li>
      </ul>
    </Div>
  )
}

const Div = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -250px;
  margin-left: -500px;

  height: 500px;
  width: 1000px;
  font-size: 20px;
  background-color: pink;
`;
