import styled from 'styled-components';
import image from '../../images/bad.jpg'

export const LandingPage = styled.div`
  background-image: url(${image});
  height: 100vh;
  width: 100%;
  font-size: 50px;
  color: red;
`

export const Button = styled.button`
	padding: 18px;
	width: 400px;
  height: 100px;
	background-color: #FD1F4A;
	color: #FAF5E6;
	text-align: center;
	letter-spacing: 0.2em;
	transition: 250ms;

	&:hover {
		background: #FBBD0D;
	}
`
