import styled from 'styled-components';

export const RecipeDetail = styled.div`
	position: relative;
	display: flex;
	flex-direction: column;
	background: #FAF5E6;
	height: auto;
	width: 750px;
	margin: 85px;
	box-shadow: 10px 15px 20px 2px #222;
`

export const Image = styled.img`
	background-size: cover;
	min-height: 400px;
`

export const Info = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	height: 100%;
`

export const Title = styled.h1`
	margin: 20px 40px 5px;
	color: #2D2C2E;
	text-align: left;
	font-size: 1.8rem;
	font-weight: normal;
	letter-spacing: 0.1em;
	text-transform: uppercase;
`

export const Score = styled.div`
	font-size: 1.2rem;
	margin: 0px 10px -5px;

	&:after {
		content: '★★★★★';
		font-size: 1.4rem;
		background: ${(props) => `linear-gradient(90deg, #FBBD0D ${props.percentage}%, #2D2C2E ${props.percentage}%)`};
		background-clip: text;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
}
`

export const HealthScore = styled.i`
	color: #9B9B9B;
	margin: 5px 30px;
`

export const Diets = styled.ul`
	display: flex;
	flex-wrap: wrap;
	margin: 5px 5px 20px;
	width: auto;
`

export const Diet = styled.li`
	flex-shrink: 0;
	padding: 2px 10px;
	margin: 5px 5px;
	width: auto;
	background-color: #2D2C2E;
	color: #FAF5E6;
	border-radius: 20px;
	font-size: 0.8rem;
	text-align: center;
`

export const DetailButton = styled.button`
	padding: 18px;
	width: 100%;
	background-color: #FD1F4A;
	color: #FAF5E6;
	text-align: center;
	letter-spacing: 0.1em;
	transition: 250ms;

	&:hover {
		background: #FBBD0D;
	}
`

