import React from 'react';
import { Link, useMatch } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components'

import SearchBar from './SearchBar.js';
import { sortCards } from '../actions';

export default function Nav() {
  const { query } = useSelector(state => state)
  const dispatch = useDispatch();
  const match = useMatch('/')

  function handleClickOrderButton(sortCriteria) {
    dispatch(sortCards(sortCriteria));
  }

  return (
    <Div >
      <SearchBar/>

      <span style={ { position: 'absolute', top: '15px', left: '320px', fontSize: '24px' } }>{ query }</span>

      <Link to='/recipe/create'>
        <CreateButton>
          CREAR
        </CreateButton>
      </Link >

      <Link to='/'>
        <HomeButton>
          HOME
        </HomeButton>
      </Link >

      {match && <Dropdown>
        <DropdownButton>Order by</DropdownButton>
        <DropdownContent>
          <p onClick={() => handleClickOrderButton('az') }>(A-Z)</p>
          <p onClick={() => handleClickOrderButton('za') }>(Z-A)</p>
          <p onClick={() => handleClickOrderButton('score') }>score</p>
        </DropdownContent>
      </Dropdown> }
    </Div>
  )
};

const Div = styled.div`
  height: 60px;
  background-color: orange;
`;

const DropdownContent = styled.div`
  display: none;
  top: 60px;
  position: absolute;
  background-color: #f1f1f1;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  z-index: 1;

  p {
  color: black;
  padding: 12px 16px;
  text-decoration: none;
  display: block;

  &:hover {background-color: #ddd;}
  }
`

const Dropdown = styled.div`
  position: absolute;
  right: 120px;
  display: inline-block;

  &:hover ${DropdownContent} {display: block;}
`

const DropdownButton = styled.div`
  background-color: #04AA6D;
  color: white;
  padding: 16px;
  font-size: 16px;
  border: none;
  width: 80px;
  height: 28px;

  &:hover {background-color: #3e8e41;}
`

const CreateButton = styled.button`
  position: absolute;
  top: 0px;
  right: 10px;

  height: 60px;
  width: 50px;
  color: black;
  background-color: crimson;
`;

const HomeButton = styled.button`
  position: absolute;
  top: 0px;
  right: 62px;

  height: 60px;
  width: 50px;
  color: black;
  background-color: crimson;
`;
